package primer;

import java.math.BigInteger;

public class NaiveCheck extends PrimeCheck {
	
	public NaiveCheck(String number) {
		super(number);
	}
	
	@Override
	public boolean check() {
		//BigInteger x = 2;
		BigInteger two = new BigInteger("2");
		BigInteger sq = approxSqrt(this.number);
		BigInteger temp = this.number.divide(two).multiply(two);
		if (this.number.equals(temp)) {
			return false;
		}
	
		for (BigInteger x = new BigInteger("3"); sq.subtract(x).doubleValue() > 0; x.add(two)) {
			temp = this.number.divide(x).multiply(x);
			if (this.number.equals(temp)) {
				return false;
			}
		}
		return true;
	}
	
	public static BigInteger approxSqrt(BigInteger x2) {
		//babylonian method 
		
		BigInteger x = x2.divide(new BigInteger("10")); //just a guess
		BigInteger xold = x2;
		BigInteger two = new BigInteger("2");
		int k = 0;
		while (xold.subtract(x).doubleValue() > 1) {
			xold = x;
			x = x2.divide(x).add(x);
			x = x.divide(two);
		}
		return x;
		
	}

	public static void main(String[] args) {
		BigInteger x = approxSqrt(new BigInteger(""));
		System.out.println(x);;
		NaiveCheck y = new NaiveCheck("");
		System.out.println(y.check());
	}

}
